export function getDirectionalVector(p1,p2){return{x:p2.x-p1.x,y:p2.y-p1.y}}export function getVectorLength(p1,p2){return p2&&(p1=getDirectionalVector(p1,p2)),Math.sqrt(Math.pow(p1.x,2)+Math.pow(p1.y,2))}export function getCenter(p1,p2){return sumVector(p1,scalarProduct(.5,getDirectionalVector(p1,p2)))}export function scalarProduct(scalar,p){return{x:scalar*p.x,y:scalar*p.y}}export function sumVector(p1,p2){return{x:p1.x+p2.x,y:p1.y+p2.y}}